#' @param sobol .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$summary} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot_tuner} \tab \tab \tab \tab \tab tuner plots \cr
#'   \code{results$tuner_table} \tab \tab \tab \tab \tab tuner table results \cr
#'   \code{results$plots_results} \tab \tab \tab \tab \tab an array of plots \cr
#'   \code{results$model} \tab \tab \tab \tab \tab The underlying \code{aov} object \cr
#'   \code{results$plots_sensitivity} \tab \tab \tab \tab \tab an array of plots \cr
#'   \code{results$table_sensitivity} \tab \tab \tab \tab \tab an array of plots \cr
#'   \code{results$pred_class} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pred_prob} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$dataset_id} \tab \tab \tab \tab \tab an output \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$summary$asDF}
#'
#' \code{as.data.frame(results$summary)}
#'
#' @export
BinaryClassification <- function(
    disable = FALSE,
    seed = 42,
    data,
    dep = NULL,
    covs = NULL,
    factors = NULL,
    stdCovs = NULL,
    encCat = NULL,
    dep_level,
    mode = "neural_network",
    hidden_neurons_tune = "range_neurons",
    penalty_tune = "range_penalty",
    fix_n_neurons = 10,
    fix_penalty = 0.1,
    min_n_neurons = 5,
    max_n_neurons = 20,
    min_penalty = -3,
    max_penalty = 3,
    mtry_tune = "range_mtry",
    trees_tune = "range_trees",
    min_n_tune = "range_min_n",
    fix_mtry = 3,
    fix_trees = 100,
    fix_min_n = 20,
    min_mtry = 3,
    max_mtry = 8,
    min_trees = 100,
    max_trees = 300,
    min_min_n = 5,
    max_min_n = 25,
    mtryx_tune = "range_mtryx",
    treesx_tune = "range_treesx",
    min_nx_tune = "range_min_nx",
    tree_depth_tune = "range_tree_depth",
    learn_ratex_tune = "range_learn_ratex",
    loss_reduction_tune = "range_loss_reduction",
    fix_mtryx = 5,
    fix_treesx = 100,
    fix_min_nx = 20,
    fix_tree_depth = 5,
    fix_learn_ratex = 0.1,
    fix_loss_reduction = 0.1,
    min_mtryx = 3,
    max_mtryx = 8,
    min_treesx = 100,
    max_treesx = 300,
    min_min_nx = 5,
    max_min_nx = 25,
    min_tree_depth = 3,
    max_tree_depth = 8,
    min_learn_ratex = -3,
    max_learn_ratex = -1,
    min_loss_reduction = -3,
    max_loss_reduction = 1.5,
    kernels,
    cost_tune = "range_cost",
    rbf_sigma_tune = "range_rbf_sigma",
    degree_tune = "range_degree",
    scale_factor_tune = "range_scale_factor",
    fix_cost = 1,
    fix_rbf_sigma = 0.01,
    fix_degree = 2,
    fix_scale_factor = 0.1,
    min_cost = -3,
    max_cost = 3,
    min_rbf_sigma = -5,
    max_rbf_sigma = 0,
    min_degree = 1,
    max_degree = 3,
    min_scale_factor = -5,
    max_scale_factor = -1,
    tuners = "none",
    metrics = "accuracy",
    plot_tuner_results = FALSE,
    accuracy = FALSE,
    bal_accuracy = FALSE,
    precision = FALSE,
    recall = FALSE,
    specificity = FALSE,
    sensitivity = FALSE,
    kap = FALSE,
    f_meas = FALSE,
    mcc = FALSE,
    j_index = FALSE,
    detection_prevalence = FALSE,
    auc_roc = FALSE,
    pr_auc = FALSE,
    gain_capture = FALSE,
    brier_score = FALSE,
    plot_roc_curve = FALSE,
    plot_pr_curve = FALSE,
    plot_gain_curve = FALSE,
    plot_lift_curve = FALSE,
    plot_dist_by_class = FALSE,
    plot_reliability_plot = FALSE,
    plot_confusion_matrix = FALSE,
    pfi = FALSE,
    shap_mean = FALSE,
    shap_dir = FALSE,
    shap_box = FALSE,
    shap_swarm = FALSE,
    olden = FALSE,
    sobol = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("BinaryClassification requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(covs)) covs <- jmvcore::resolveQuo(jmvcore::enquo(covs))
    if ( ! missing(factors)) factors <- jmvcore::resolveQuo(jmvcore::enquo(factors))
    if ( ! missing(stdCovs)) stdCovs <- jmvcore::resolveQuo(jmvcore::enquo(stdCovs))
    if ( ! missing(encCat)) encCat <- jmvcore::resolveQuo(jmvcore::enquo(encCat))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(covs), covs, NULL),
            `if`( ! missing(factors), factors, NULL),
            `if`( ! missing(stdCovs), stdCovs, NULL),
            `if`( ! missing(encCat), encCat, NULL))

    for (v in dep) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in factors) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- BinaryClassificationOptions$new(
        disable = disable,
        seed = seed,
        dep = dep,
        covs = covs,
        factors = factors,
        stdCovs = stdCovs,
        encCat = encCat,
        dep_level = dep_level,
        mode = mode,
        hidden_neurons_tune = hidden_neurons_tune,
        penalty_tune = penalty_tune,
        fix_n_neurons = fix_n_neurons,
        fix_penalty = fix_penalty,
        min_n_neurons = min_n_neurons,
        max_n_neurons = max_n_neurons,
        min_penalty = min_penalty,
        max_penalty = max_penalty,
        mtry_tune = mtry_tune,
        trees_tune = trees_tune,
        min_n_tune = min_n_tune,
        fix_mtry = fix_mtry,
        fix_trees = fix_trees,
        fix_min_n = fix_min_n,
        min_mtry = min_mtry,
        max_mtry = max_mtry,
        min_trees = min_trees,
        max_trees = max_trees,
        min_min_n = min_min_n,
        max_min_n = max_min_n,
        mtryx_tune = mtryx_tune,
        treesx_tune = treesx_tune,
        min_nx_tune = min_nx_tune,
        tree_depth_tune = tree_depth_tune,
        learn_ratex_tune = learn_ratex_tune,
        loss_reduction_tune = loss_reduction_tune,
        fix_mtryx = fix_mtryx,
        fix_treesx = fix_treesx,
        fix_min_nx = fix_min_nx,
        fix_tree_depth = fix_tree_depth,
        fix_learn_ratex = fix_learn_ratex,
        fix_loss_reduction = fix_loss_reduction,
        min_mtryx = min_mtryx,
        max_mtryx = max_mtryx,
        min_treesx = min_treesx,
        max_treesx = max_treesx,
        min_min_nx = min_min_nx,
        max_min_nx = max_min_nx,
        min_tree_depth = min_tree_depth,
        max_tree_depth = max_tree_depth,
        min_learn_ratex = min_learn_ratex,
        max_learn_ratex = max_learn_ratex,
        min_loss_reduction = min_loss_reduction,
        max_loss_reduction = max_loss_reduction,
        kernels = kernels,
        cost_tune = cost_tune,
        rbf_sigma_tune = rbf_sigma_tune,
        degree_tune = degree_tune,
        scale_factor_tune = scale_factor_tune,
        fix_cost = fix_cost,
        fix_rbf_sigma = fix_rbf_sigma,
        fix_degree = fix_degree,
        fix_scale_factor = fix_scale_factor,
        min_cost = min_cost,
        max_cost = max_cost,
        min_rbf_sigma = min_rbf_sigma,
        max_rbf_sigma = max_rbf_sigma,
        min_degree = min_degree,
        max_degree = max_degree,
        min_scale_factor = min_scale_factor,
        max_scale_factor = max_scale_factor,
        tuners = tuners,
        metrics = metrics,
        plot_tuner_results = plot_tuner_results,
        accuracy = accuracy,
        bal_accuracy = bal_accuracy,
        precision = precision,
        recall = recall,
        specificity = specificity,
        sensitivity = sensitivity,
        kap = kap,
        f_meas = f_meas,
        mcc = mcc,
        j_index = j_index,
        detection_prevalence = detection_prevalence,
        auc_roc = auc_roc,
        pr_auc = pr_auc,
        gain_capture = gain_capture,
        brier_score = brier_score,
        plot_roc_curve = plot_roc_curve,
        plot_pr_curve = plot_pr_curve,
        plot_gain_curve = plot_gain_curve,
        plot_lift_curve = plot_lift_curve,
        plot_dist_by_class = plot_dist_by_class,
        plot_reliability_plot = plot_reliability_plot,
        plot_confusion_matrix = plot_confusion_matrix,
        pfi = pfi,
        shap_mean = shap_mean,
        shap_dir = shap_dir,
        shap_box = shap_box,
        shap_swarm = shap_swarm,
        olden = olden,
        sobol = sobol)

    analysis <- BinaryClassificationClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

